mapper.select:
SELECT A.BZ_NAME AS BZNAME , A.BZ_INTRODUCE AS BZINTRODUCE ,A.BZ_FL AS BZFL , A.SRC_NAME AS SRCNAME , A.ID AS ID , A.CREATEDATE AS CREATEDATE , A.POPULATION_FLAG/(SELECT MAX(RESOURCE.POPULATION_FLAG) FROM CJXJ_RESOURCE_TABLE RESOURCE)*100 AS POPULATION FROM CJXJ_RESOURCE_TABLE A WHERE 1=1 AND A.BZ_FL=#{bzfl}
AND A.BZ_NAME LIKE "%"#{bzname}"%"
ORDER BY A.ID ASC
LIMIT #{start},#{limit}

mapper.update:
UPDATE  CJXJ_RESOURCE_TABLE CJXJ_RESOURCE  
SET  CJXJ_RESOURCE.POPULATION_FLAG = CJXJ_RESOURCE.POPULATION_FLAG + 1 
WHERE CJXJ_RESOURCE.ID =#{id}
